body.raw:
{
    "channels": [
        {
            "name": "viber",
            "from": " {{ from }}",
            "meta": {
                "category": "promotional"
            }
        }
    ],
    "recipient": {
        "to": [
            " {{ receiver }}"
        ]
    },
    "message": {
        "type": "interactive",
        "payload": {
            "header": {
                "type": "image",
                "payload": {
                    "url": "https://www.gstatic.com/webp/gallery/4.sm.jpg"
                }
            },
            "body": {
                "type": "text",
                "payload": {
                    "text": "Welcome to viber messaging!!"
                }
            },
            "choices": [
                {
                    "type": "button",
                    "payload": {
                        "title": "click here",
                        "url": "http://www.viber.com"
                    }
                }

            ]
        }
    }
}
